package natnet

import (
	"strconv"
	"strings"
	"testing"

	"github.com/stretchr/testify/assert"
)

func parseIssueString(input string) []byte {
	byteStrings := strings.Fields(input)
	result := make([]byte, len(byteStrings))
	var x uint64
	for i, b := range byteStrings {
		x, _ = strconv.ParseUint(b, 10, 8)
		result[i] = byte(x)
	}
	return result
}

func TestParseIssueString(t *testing.T) {
	parsed := parseIssueString("7 0 74 3 71 204 42")
	expected := []byte{7, 0, 74, 3, 71, 204, 42}
	assert.Equal(t, parsed, expected, "Should parse correctly")
}

func TestPanicsOnCopletelyBrokenData(t *testing.T) {
	bin := []byte{7, 0, 74, 3, 71, 204, 42}
	assert.Panics(t, func() {
		parsePacket(bin)
	}, "Should panic")
}

